import ScopeNode from "./scopeTree";

/**
 * This is the core formatter. It contains most of the logic behind the formatting,
 * and it is written in such a way that it is easily extendable to other programming
 * languages. To create a formatter for a new programming language, extend this class
 * and follow the JavaFormatter as an example.
 */
export default class AFormatter {

  /**
   * Create a new AFormatter (abstract formatter).
   *
   * @constructor
   * @param formatUnit The token to be used for line indentations.
   */
  constructor(formatUnit) {
    /**
     * @private
     */
    this.formatUnit = formatUnit

    /**
     * @private
     */
    this.fullCodeArray = ''

    /**
     * @private
     */
    this.startSelection = 0

    /**
     * @private
     */
    this.endSelection = 0

    /**
     * @private
     */
    this.snippetOffset = 0

    /**
     * @private
     */
    this.bodyCommentToken = ''

    /**
     * @private
     */
    this.simpleCommentToken = ''

    /**
     * @private
     */
    this.expressionIdentifier = null

    /**
     * @private
     */
    this.scopeEnterFunc = null

    /**
     * @private
     */
    this.scopeExitFunc = null

    /**
     * @private
     */
    this.identifyMethodSigFunc = null

    /**
     * @private
     */
    this.identifySpecialStatement = null
  }

  /**
   * Format a string of code. The string will be cut into lines and lines
   * will be indented accordingly to their scope.
   *
   * As a reference, look at the format function in the JavaFormatter.
   *
   * @param code String of code to format.
   * @param expressionIdentifier Function that identifies if a line qualifies as an expression:
   * - A line that ends with a termination token (e.g. ';')
   * - A line that defines a scope start (e.g. '\{')
   * - A line that defines a scope end (e.g. '\}')
   * - A line that starts with a special character (e.g. '@')
   * - A line that starts with a comment (e.g. '//')
   * - An empty line (e.g. '')
   * @param scopeEnterFunc Function that identifies if a new scope is entered in a line.
   * @param scopeExitFunc Function that identifies if a scope is exited in a line.
   * @param formatCommentsFunc Function that formats comments (e.g. add space before body and
   *                                 end comment in Javadoc)
   * @returns {Array} An array of formatted lines.
   */
  format(code, expressionIdentifier, scopeEnterFunc, scopeExitFunc, formatCommentsFunc) {
    return this.formatSnippet(code, null, null, null, expressionIdentifier, scopeEnterFunc,
      scopeExitFunc, formatCommentsFunc, null, null, null, null)
  }

  /**
   * A slight variation of format(codeString). Useful if you want to display
   * a code snippet around a selection of lines.
   *
   * As a reference, look at the formatSnippet function in the JavaFormatter.
   *
   * In addition to indenting lines, formatSnippet takes a selection as a
   * start and end row in a large slab of code and cuts out a snippet of
   * code around this selection. The start and end of the snippet is based
   * on an offset that is provided as a parameter. The offset with the start
   * and end of the selection create a sort of range from which the snippet
   * is taken.
   *
   * In the example below, the selection is identified to belong to test2()
   * and thus only test2() is returned. If the method is longer than the
   * offset, than only the part within the offset will be returned. No code
   * is added to the range with the exception of comment lines above the
   * selection, to close unfinished comments.
   *
   * @example
   * <caption>Selection start row: 11 ---- Selection end row: 11 ---- Offset: 6 ----> Snippet range: [11 - 6, 11 + 6] = [5, 17]</caption>
   *
   * START:
   * 1.  @Test
   * 2.  public void test1() {
   * 3.      System.out.println("Test 1");
   * 4.  }
   * 5.
   * 6.  // ------------------
   * 7.  // Perform test 2.
   * 8.  // ------------------
   * 9. @Test
   * 10. public void test2() {
   * 11.     System.out.println("Test 2");
   * 12. }
   * 13.
   * 14. @Test
   * 15. public void test3() {
   * 16.     System.out.println("Test 3");
   * 17. }
   * 18. ...
   *
   * RESULT:
   * 6.  // ------------------
   * 7.  // Perform test 2.
   * 8.  // ------------------
   * 9. @Test
   * 10. public void test2() {
   * 11.     System.out.println("Test 1");
   * 12. }
   *
   * @param code The original code base in which the selection is.
   * @param startRow The start row of the selection in the code base.
   * @param endRow The end row of the selection in the code base.
   * @param offset The offset the defines the range on which to base the
   *               snippet.
   * @param expressionIdentifier Function that identifies if a line qualifies as an expression:
   * An expression is defined as:
   * - A line that ends with a termination token (e.g. ';')
   * - A line that defines a scope start (e.g. '\{')
   * - A line that defines a scope end (e.g. '\}')
   * - A line that starts with a special character (e.g. '@')
   * - A line that starts with a comment (e.g. '//')
   * - An empty line (e.g. '')
   * @param scopeEnterFunc Function that identifies if a new scope is entered in a line.
   * @param scopeExitFunc Function that identifies if a scope is exited in a line.
   * @param formatCommentsFunc Function that formats comments (e.g. add space before body and
   *                                 end comment in Javadoc)
   * @param identifyMethodSigFunc Function that identifies if a line is a method signature.
   * @param identifySpecialStatement Function that identifies if a line contains a special
   *                                 statement (e.g. comment or '@' in Java)
   * @param bodyCommentToken The token for 'body comments' (e.g. '*' in Java)
   * @param simpleCommentToken Simple comment token (e.g. '//')
   * @returns {Array} An array of formatted lines that form the snippet, separated
   *              into prefix selection and suffix, as well as the start and
   *              end lines of the snippet in the original code base.
   */
  formatSnippet(code, startRow, endRow, offset, expressionIdentifier, scopeEnterFunc,
                scopeExitFunc, formatCommentsFunc, identifyMethodSigFunc, identifySpecialStatement,
                bodyCommentToken, simpleCommentToken) {
    // Initialize
    this.fullCodeArray = code.split('\n')
    this.startSelection = startRow
    this.endSelection = endRow
    this.snippetOffset = offset
    this.expressionIdentifier = expressionIdentifier
    this.scopeEnterFunc = scopeEnterFunc
    this.scopeExitFunc = scopeExitFunc
    this.formatCommentFunc = formatCommentsFunc
    this.identifyMethodSigFunc = identifyMethodSigFunc
    this.identifySpecialStatement = identifySpecialStatement
    this.bodyCommentToken = bodyCommentToken
    this.simpleCommentToken = simpleCommentToken

    let codeArray = this.fullCodeArray

    // In case we have a snippet, find the snippet
    let snippetPresent = this.startSelection !== null && this.endSelection !== null
      && offset !== null && this.startSelection !== -1 && this.endSelection !== -1
      && offset !== -1

    let snippet = []
    if (snippetPresent) {
      snippet = [this.fullCodeArray.slice(this.startSelection - 1, this.endSelection),
        this.fullCodeArray.slice(this.startSelection - offset - 1,
          this.endSelection + offset)]
      codeArray = this._preFormatSnippet(snippet[1], snippet[0])
    }

    // Build and balance the scope tree
    codeArray = codeArray.map(line => line.trim())
    let scopeTree = new ScopeNode(null, null)

    // copy array because it is consumed
    scopeTree.build(codeArray.slice(), 0, this.scopeEnterFunc, this.scopeExitFunc)
    scopeTree.balance()

    // Init formatted array
    let formattedArray = []
    for (let i = 0; i < codeArray.length; i++) {
      formattedArray[i] = ""
    }
    this._preFormatArray(formattedArray, scopeTree)

    // Fill formatted array with code
    for (let i = 0; i < codeArray.length; i++) {
      formattedArray[i] += codeArray[i]
    }

    // Add spaces after lines that do not qualify as an expression (e.g. let str = "hello" +)
    for (let i = 0; i < formattedArray.length; i++) {
      if (!this.expressionIdentifier(formattedArray, i) && formattedArray.length > i + 1) {
        if (this.scopeEnterFunc(formattedArray, i + 1) === null
          && this.scopeExitFunc(formattedArray, i + 1) === null) {
          formattedArray[i + 1] = this.formatUnit + formattedArray[i + 1]
        }
      }
    }

    // Format comments correctly (some languages have special formatting for comments, e.g. Javadoc)
    formattedArray = this.formatCommentFunc(formattedArray)

    // If we don't have a snippet, return the result now
    if (!snippetPresent) {
      formattedArray = this._trimBeginning(formattedArray)
      return this._trimEnd(formattedArray)
    }

    // Format prefix and suffix
    let selection = this._splitSelection(formattedArray, snippet[0])
    let prefixResult = this._formatPrefix(scopeTree, selection[0],
      this.startSelection - selection[0].length)
    let suffixResult = this._formatSuffix(selection[2],
      this.endSelection + selection[2].length)
    let range = [prefixResult[1], suffixResult[1]]

    // Turn array into string
    let selectionString = ""
    if (selection[1].length > 0) {
      selectionString = selection[1].reduce(((acc, line) => acc + '\n' + line))
    }

    return [prefixResult[0], selectionString, suffixResult[0], range]
  }

  /**
   * Performs the following preliminary tasks:
   *  - Fills open comments
   *  - Removes extra method signatures (===> scope break so it will be removed later)
   *
   * @param snippet The snippet, as an array of lines, to work on.
   * @param selection The selection, as an array of lines, within the snippet.
   * @returns {Array} Array of pre-formatted lines.
   * @private
   */
  _preFormatSnippet(snippet, selection) {
    let splitSnippet = this._splitSelection(snippet, selection)
    let prefixArray = this._handleOpenComments(splitSnippet[0],
      this.startSelection - this.snippetOffset - 1)
    prefixArray = this._removeExtraMethodSigAbove(prefixArray)
    let suffixArray = this._removeExtraMethodSigBelow(splitSnippet[2])
    return prefixArray.concat(splitSnippet[1].concat(suffixArray))
  }

  /**
   * Format the prefix of a snippet. The prefix is defines as all lines above the
   * selection. Performs the following tasks:
   *  - Removes scopes that close but never open.
   *  - Removes empty lines at beginning of snippet.
   *
   * @param scopeTree The scope tree of the snippet.
   * @param array The formatted snippet as an array of lines.
   * @param oldStart The old start of the snippet in the original file
   *                 (line number, starting with 1 not 0).
   * @returns {Array} Formatted prefix string with the new beginning of the snippet
   *                in the original file (again index starting with 1).
   * @private
   */
  _formatPrefix(scopeTree, array, oldStart) {
    let originalLength = array.length

    let limit = scopeTree.getChildren()
      .filter(node => node.getStart() === null && node.getEnd() !== null)
      .filter(node => node.getEnd() < array.length)
      .reduce((limit, current) => Math.max(limit, current.getEnd()), -1)

    let result = []
    for (let i = limit + 1; i < array.length; i++) {
      result.push(array[i])
    }

    result = this._trimBeginning(result)
    let offset = originalLength - result.length

    if (result.length > 0) {
      result = result.reduce((acc, line) => acc + '\n' + line)
    } else {
      result = ""
    }

    return [result, oldStart + offset]
  }

  /**
   * Format the suffix of a snippet. The suffix is defines as all lines below the
   * selection. Performs the following tasks:
   *  - Removes methods and comments belonging to method below selection.
   *  - Removes empty lines at end of snippet.
   *
   * @param codeArray The formatted suffix as an array of lines.
   * @param oldEnd The old end of the snippet in the original file
   *               (line number, starting with 1 not 0).
   * @returns {Array} Formatted suffix string with the new end of the snippet
   *                in the original file (again index starting with 1).
   * @private
   */
  _formatSuffix(codeArray, oldEnd) {
    if (codeArray.length === 0) {
      return codeArray
    }

    let originalLength = codeArray.length
    let codeFound = false
    let index = codeArray.length - 1

    while (index >= 0) {
      let line = codeArray[index].trim()
      if (this.identifySpecialStatement(line) && line !== ''
        && (!line.trim().startsWith(this.simpleCommentToken.trim()) || !codeFound)) {
        codeArray.splice(index, 1)
      } else if (line !== '') {
        codeFound = true
      }

      index--
    }

    codeArray = this._trimEnd(codeArray)
    let offset = originalLength - codeArray.length

    // Make sure we don't reduce an empty array
    if (codeArray.length === 0) {
      return codeArray
    }

    return [codeArray.reduce(((acc, line) => acc + '\n' + line)), oldEnd - offset]
  }

  /**
   * Pre-formats an empty array by adding the indentations
   * (the specified format unit).
   *
   * @param array Array of lines of code.
   * @param node Node of the scope tree.
   * @private
   */
  _preFormatArray(array, node) {
    let start = node.getStart()
    let end = node.getEnd()

    if (start !== null && end !== null) {
      this._fillBucketRange(array, start + 1, end - 1)
    } else if (start === null && end !== null) {
      this._fillBucketRange(array, 0, end - 1)
    } else if (start !== null && end === null) {
      this._fillBucketRange(array, start + 1, array.length - 1)
    }

    node.getChildren().forEach(child => this._preFormatArray(array, child))
  }

  /**
   * Adds indentations to the array from the start to the end index, inclusive.
   *
   * @param array Array of lines of code.
   * @param start Start index of where to add indentations.
   * @param end End index of where to stop adding indentations.
   * @private
   */
  _fillBucketRange(array, start, end) {
    for (let i = start; i <= end; i++) {
      array[i] += this.formatUnit
    }
  }

  /**
   * If an open comment is met, take all lines necessary to close it
   * from the original file and append them to the snippet.
   *
   * @param codeArray Array of lines of code.
   * @param startLine The start line of the snippet in the original file.
   * @returns {Array} A snippet as an array of lines of code with no more
   *              open comments.
   * @private
   */
  _handleOpenComments(codeArray, startLine) {
    if (codeArray.length > 0 && startLine - 1 < this.fullCodeArray.length
      && codeArray[0].trim().startsWith(this.bodyCommentToken)) {
      codeArray.unshift(this.fullCodeArray[startLine - 1])
      return this._handleOpenComments(codeArray, startLine - 1)
    } else {
      return codeArray
    }
  }

  /**
   * Remove method signatures in the prefix that do not belong to the selection.
   *
   * @param codeArray Array of lines of code (prefix).
   * @returns {Array} Prefix with no more extra method signatures.
   * @private
   */
  _removeExtraMethodSigAbove(codeArray) {
    if (codeArray.length === 0) {
      return codeArray
    }

    let methodSigCount = 0
    let index = codeArray.length - 1
    while (index >= 0) {
      let line = codeArray[index].trim()
      if (this.identifyMethodSigFunc(line) && methodSigCount === 0) {
        methodSigCount++
      } else if (this.identifyMethodSigFunc(line) && methodSigCount === 1) {
        codeArray.splice(index, 1)
        methodSigCount++
      } else if (this.scopeEnterFunc([line], 0) !== null && index - 1 > 0
        && methodSigCount === 1) {
        if (this.identifyMethodSigFunc(codeArray[index - 1])) {
          codeArray.splice(index, 1)
          methodSigCount++
        }
      } else if (methodSigCount > 1) {
        codeArray.splice(index, 1)
      }

      index--
    }

    if (this.scopeEnterFunc([codeArray[0].trim()], 0) === 0) {
      codeArray.shift()
    }

    return codeArray
  }

  /**
   * Remove method signatures in the suffix that do not belong to the selection.
   *
   * @param codeArray Array of lines of code (suffix).
   * @returns {Array} Suffix with no more extra method signatures.
   * @private
   */
  _removeExtraMethodSigBelow(codeArray) {
    let index = 0
    let found = false
    while (index < codeArray.length) {
      let line = codeArray[index].trim()
      if (this.identifyMethodSigFunc(line) || found) {
        codeArray.splice(index, 1)
        found = true
      } else {
        index++
      }
    }

    return codeArray
  }

  /**
   * Removes empty lines at beginning of snippet.
   *
   * @param codeArray Array of lines of code.
   * @returns {Array} Code array with no empty lines at beginning.
   * @private
   */
  _trimBeginning(codeArray) {
    let canTrim = true

    while (canTrim && codeArray.length > 0) {
      let line = codeArray[0]
      if (line === "\n" || line.trim() === "") {
        codeArray.shift();
      } else {
        canTrim = false
      }
    }

    return codeArray
  }

  /**
   * Removes empty lines at end of snippet.
   *
   * @param codeArray Array of lines of code.
   * @returns {Array} Code array with no empty lines at end.
   * @private
   */
  _trimEnd(codeArray) {
    let canTrim = true

    while (canTrim && codeArray.length > 0) {
      let line = codeArray[codeArray.length - 1]
      if (line === "\n" || line.trim() === "") {
        codeArray.pop()
      } else {
        canTrim = false
      }
    }

    return codeArray
  }

  /**
   * Splits the code array into three parts: prefix, selection, suffix.
   *
   * Prefix: All lines above the selection.
   * Suffix: All lines below the selection.
   *
   * @param codeArray Array of lines of code.
   * @param selection Selection to split codeArray on.
   * @returns {Array} An array containing again the prefix, selection and suffix as
   *                arrays of lines of code.
   * @private
   */
  _splitSelection(codeArray, selection) {
    let startArray = []
    let selectionArray = []
    let endArray = []
    let selectionFound = false
    let selectionDone = false

    codeArray.forEach(line => {
      if (selection.length !== 0) {
        if (!selectionDone && selection.reduce((result, sLine) => result
            || line.includes(sLine.trim()), false)) {
          selectionFound = true
          selectionArray.push(line)
        } else if (selectionFound) {
          endArray.push(line)
          selectionDone = true
        } else {
          startArray.push(line)
        }
      }
    })

    return [startArray, selectionArray, endArray];
  }
}

/**
 * A scope tree is a tree that captures the scope of a piece of code. A node
 * contains the start and end of the scope. If start or end is null then the
 * start or end is not in the code. A parent node contains all scopes inside
 * it as children nodes.
 */
export default class ScopeTree {

  /**
   * Creates a new node.
   *
   * @constructor
   * @param parent The parent node.
   * @param start The start of the scope of this node.
   */
  constructor(parent, start) {
    /**
     * @private
     */
    this.parent = parent

    /**
     * @private
     */
    this.children = []

    /**
     * @private
     */
    this.start = start

    /**
     * @private
     */
    this.end = null
  }

  /**
   * Get the start of the scope.
   *
   * @returns {Number} The start of the scope, or null if not defined.
   */
  getStart() {
    return this.start
  }

  /**
   * Get the end of the scope.
   *
   * @returns {Number} The end of the scope, or null if not defined.
   */
  getEnd() {
    return this.end
  }

  /**
   * Get the parent of this node. Returns null if this is the root node.
   *
   * @returns {ScopeTree} The parent of this node, or null if this is the root node.
   */
  getParent() {
    return this.parent
  }

  /**
   * Get an array of all children of this node.
   *
   * @returns {Array} An array of all children of this node.
   */
  getChildren() {
    return this.children
  }

  /**
   * Add a child to this node.
   *
   * @param child The child to add to this node, should also be of type ScopeTree.
   */
  addChild(child) {
    this.children.push(child)
  }

  /**
   * Close this scope. This means the end of the scope has been reached.
   *
   * @param line The line that the scope ends at.
   */
  close(line) {
    this.end = line
  }

  /**
   * Returns true if the scope of this node is closed (meaning the scope has
   * a start and an end point)
   *
   * @returns {Boolean} True if the scope for this node is defined, else false.
   */
  isClosed() {
    return this.start !== null && this.end !== null
  }

  /**
   * Balance the tree. This enforces an ordering of the nodes in the tree such
   * that parent nodes are always 1 scope above their child nodes.
   *
   * By nature, the tree will be built in a balanced fashion. However because
   * we might be dealing with snippets of code scope entries or exits (i.e. brackets)
   * might be missing, which puts the tree out of balance. This method fixes that.
   *
   * Particularly, this moves all left siblings of a node that only has an end
   * but not a start under it, making them his children.
   *
   * @example
   *  <-> : scope is open and closed
   *  <-  : scope is only opened but not closed
   *   -> : scope is only closed but never opened
   *
   * IMBALANCED TREE                BALANCED TREE
   *  -root-                         -root-
   *    |                              |
   *    |---------------               |-----
   *    |    |    |    |               |    |
   *    |    |    |    |               |    |
   *   <->  <->   ->  <-    =======>   ->  <-
   *                                   |
   *    ^    ^    ^    ^               |-----
   *    |    |    |    |               |    |
   *   ok   ok   not  ok               |    |
   *             ok                   <->  <->
   */
  balance() {
    for (let i = 0; i < this.getChildren().length; i++) {
      let child = this.getChildren()[i]

      // Check if there is a lone closing scope
      if (child.getStart() === null && child.getEnd() !== null) {

        /* Pretend this lone closing scope had a matching opening scope at the
         * beginning and turn all previous siblings into children of the closing
         * scope.
         */
        for (let j = 0; j < i; j++) {
          let sibling = this.getChildren()[0]
          this.getChildren().splice(0, 1)
          child.addChild(sibling)
        }
      }

      child.balance()
    }
  }

  /**
   * Builds the scope tree. This function is designed in such a way that it can
   * build scope trees over a variety of different programming languages. That
   * is why as a parameter, the function takes two functions, each which
   * determine where the scope starts or ends in a current line, if it does at
   * all. These functions NEED to have the following signatures:
   *
   * Index:Integer scopeEnterFunc(arrayOfAllLines:Array, lineToCheckIndex:Integer)
   * Index:Integer scopeExitFunc(arrayOfAllLines:Array, lineToCheckIndex:Integer)
   *
   * Both functions take the full array of lines and the index of the line to
   * examine, and return the column within the line at which the scope is entered
   * or exited. If there is no scope change in the line, null must be returned.
   *
   * @param lines Array of lines to build the scope tree over.
   * @param index Current line number.
   * @param scopeEnterFunc Function with the above signature. It takes the full
   *        array of lines and the index of the line to examine, and returns
   *        the column within the line at which the scope is entered. If no scope
   *        is entered in the line, null must be returned.
   * @param scopeExitFunc Function with the above signature. It takes the full
   *        array of lines and the index of the line to examine, and returns
   *        the column within the line at which the scope is exited. If no scope
   *        is exited in the line, null must be returned.
   * @returns {ScopeTree} The root node of the scope tree.
   */
  build(lines, index, scopeEnterFunc, scopeExitFunc) {
    let node = this
    if (lines.length === 0) {
      if (node.getParent() !== null) {
        return node.getParent().build([], index, scopeEnterFunc, scopeExitFunc);
      } else {
        return node
      }
    }

    let enterIndex = scopeEnterFunc(lines, 0)
    let exitIndex = scopeExitFunc(lines, 0)
    let line = lines.shift()

    if (enterIndex !== null && (enterIndex < exitIndex || exitIndex === null)) {
      let child = new ScopeTree(node, index)
      node.addChild(child)

      let remaining = line.substr(enterIndex + 1);
      if (scopeEnterFunc([remaining], 0) !== null) {
        lines.unshift(remaining)
        return child.build(lines, index, scopeEnterFunc, scopeExitFunc)
      } else {
        node = child // This allows us to check if the scope is closed in the
                     // same line and if not we pass on the child as the next node
      }
    } else if (enterIndex !== null && exitIndex !== null) {
      // Check for this case: } foo {
      if (node.getParent() === null) {
        let child = new ScopeTree(node, null)
        child.close(index)
        node.addChild(child)
        node = child
      } else {
        node.close(index)
      }

      let remaining = line.substr(exitIndex + 1)
      lines.unshift(remaining)
      return node.getParent().build(lines, index, scopeEnterFunc, scopeExitFunc)
    }

    if (exitIndex !== null) {
      if (node.getParent() === null) {
        let child = new ScopeTree(node, null)
        child.close(index)
        node.addChild(child)
        node = child // This avoids a null pointer exception when we call
                     // node.getParent() below
      } else {
        node.close(index)
      }

      let remaining = line.substr(exitIndex + 1)

      if (scopeExitFunc([remaining], 0) !== null) {
        lines.unshift(remaining)
        return node.getParent().build(lines, index, scopeEnterFunc, scopeExitFunc)
      } else {
        node = node.getParent()
      }
    }

    return node.build(lines, index + 1, scopeEnterFunc, scopeExitFunc)
  }
}

import Formatter from "../core/formatter"

var SCOPE_ENTER_TOKEN = '{'
var SCOPE_EXIT_TOKEN = '}'
var EXPRESSION_TERMINATION_TOKEN = ';'
var ANNOTATION_TOKEN = '@'
var COMMENT_START_TOKEN = '/**'
var COMMENT_START_TOKEN_2 = '/*'
var COMMENT_BODY_TOKEN = '*'
var COMMENT_END_TOKEN = '*/'
var COMMENT_SIMPLE_TOKEN = '//'
var COMMENT_TOKENS = [COMMENT_START_TOKEN, COMMENT_START_TOKEN_2, COMMENT_BODY_TOKEN,
  COMMENT_END_TOKEN, COMMENT_SIMPLE_TOKEN]
var PROTECTED_NON_METHOD_TOKENS = ['return', 'new']

/**
 * JavaFormatter is the auto-formatter implementation for Java.
 * @extends {AFormatter}
 */
export default class JavaFormatter extends Formatter {
  /**
   * Create a new JavaFormatter
   *
   * @constructor
   * @param formatUnit The token to be used for line indentations.
   */
  constructor(formatUnit) {
    super(formatUnit)

    /**
     * @private
     */
    this.methodSigRegex = new RegExp("^(public |private |protected |static |final " +
      "|native |synchronized |abstract |transient )*(<.*>\\s+)?\\w+(<.*>|\\[.*\\])" +
      "?\\s+\\w+\\s*\\(.*$")
  }

  /**
   * Format a string of code. The string will be cut into lines and lines
   * will be indented accordingly to their scope.
   *
   * @param codeString The string of code to format.
   * @returns {Array} An array of formatted lines.
   */
  format(codeString) {
    return this.formatSnippet(codeString, null, null, null)
  }

  /**
   * A slight variation of format(codeString). Useful if you want to display
   * a code snippet around a selection of lines.
   *
   * In addition to indenting lines, formatSnippet takes a selection as a
   * start and end row in a large slab of code and cuts out a snippet of
   * code around this selection. The start and end of the snippet is based
   * on an offset that is provided as a parameter. The offset with the start
   * and end of the selection create a sort of range from which the snippet
   * is taken.
   *
   * In the example below, the selection is identified to belong to test2()
   * and thus only test2() is returned. If the method is longer than the
   * offset, than only the part within the offset will be returned. No code
   * is added to the range with the exception of comment lines above the
   * selection, to close unfinished comments.
   *
   * @example
   * <caption>Selection start row: 11 ---- Selection end row: 11 ---- Offset: 6 ----> Snippet range: [11 - 6, 11 + 6] = [5, 17]</caption>
   *
   *  START:
   * 1.  @Test
   * 2.  public void test1() {
   * 3.      System.out.println("Test 1");
   * 4.  }
   * 5.
   * 6.  // ------------------
   * 7.  // Perform test 2.
   * 8.  // ------------------
   * 9. @Test
   * 10. public void test2() {
   * 11.     System.out.println("Test 2");
   * 12. }
   * 13.
   * 14. @Test
   * 15. public void test3() {
   * 16.     System.out.println("Test 3");
   * 17. }
   * 18. ...
   *
   * RESULT:
   * 6.  // ------------------
   * 7.  // Perform test 2.
   * 8.  // ------------------
   * 9. @Test
   * 10. public void test2() {
   * 11.     System.out.println("Test 1");
   * 12. }
   *
   * @param code The original code base in which the selection is.
   * @param startRow The start row of the selection in the code base.
   * @param endRow The end row of the selection in the code base.
   * @param offset The offset the defines the range on which to base the
   *               snippet.
   * @returns {Array} An array of formatted lines that form the snippet, separated
   *              into prefix selection and suffix, as well as the start and
   *              end lines of the snippet in the original code base.
   */
  formatSnippet(code, startRow, endRow, offset) {
    return super.formatSnippet(code, startRow, endRow, offset,
      ((codeArray, index) => this._expressionIdentifier(codeArray, index)),
      ((lines, index) => this._scopeEnterFunc(lines, index)),
      ((lines, index) => this._scopeExitFunc(lines, index)),
      (array => this._formatJavadoc(array)),
      (line => this._checkForFunction(line)),
      (line => this._checkForSpecialStatement(line)),
      COMMENT_BODY_TOKEN, COMMENT_SIMPLE_TOKEN)
  }

  /**
   * Checks if a line identified by an index in an array qualifies
   * as an expression.
   *
   * An expression is defined as:
   * - A line that ends with a termination token (e.g. ';')
   * - A line that defines a scope start (e.g. '\{')
   * - A line that defines a scope end (e.g. '\}')
   * - A line that starts with a special character (e.g. '@')
   * - A line that starts with a comment (e.g. '//')
   * - An empty line (e.g. '')
   *
   * @param codeArray An array of lines of code.
   * @param index The index of the relevant line in the code array.
   * @returns {Boolean} True if the line qualifies as an expression, else false.
   * @private
   */
  _expressionIdentifier(codeArray, index) {
    if (codeArray.length > index) {
      let line = codeArray[index].replace('\n', '').trim()
      return line.endsWith(EXPRESSION_TERMINATION_TOKEN)
        || this._scopeEnterFunc([line], 0) !== null
        || this._scopeExitFunc([line], 0) !== null
        || this._checkForSpecialStatement(line)
    }

    return false
  }

  /**
   * Checks if a line identified by an index in an array starts a new scope.
   * Example: 'if (foo) {'
   *
   * @param codeArray An array of lines of code.
   * @param index The index of the relevant line in the code array.
   * @returns {Number} The position in the line where the new scope starts or null
   *              if this line does not start a new scope.
   * @private
   */
  _scopeEnterFunc(codeArray, index) {
    return this._identifyScope(codeArray, index, SCOPE_ENTER_TOKEN)
  }

  /**
   * Checks if a line identified by an index in an array ends an existing scope.
   * Example: '}'
   *
   * @param codeArray An array of lines of code.
   * @param index The index of the relevant line in the code array.
   * @returns {Number} The position in the line where the scope ends or null
   *              if this line does not end a scope.
   * @private
   */
  _scopeExitFunc(codeArray, index) {
    return this._identifyScope(codeArray, index, SCOPE_EXIT_TOKEN)
  }

  /**
   * Adds a space before Javadoc comments if they are a body for end comment.
   *
   * @param codeArray The already formatted code array.
   * @returns {*} The formatted code array with spaces for Javadoc.
   * @private
   */
  _formatJavadoc(codeArray) {
    for (let i = 0; i < codeArray.length; i++) {
      let lineTemp = codeArray[i].trim()
      if (lineTemp.startsWith(COMMENT_BODY_TOKEN) || lineTemp.startsWith(COMMENT_END_TOKEN)) {
        codeArray[i] = " " + codeArray[i];
      }
    }
    return codeArray
  }

  /**
   * Helper method for _scopeEnterFunc and _scopeExitFunc.
   *
   * @param codeArray An array of lines of code.
   * @param index The index of the relevant line in the code array.
   * @param token The token to find in the line.
   * @returns {Number} The position in the line where the scope starts or ends or
   *              null if this line does neither.
   * @private
   */
  _identifyScope(codeArray, index, token) {
    if (codeArray.length > 0) {
      let scopeIndex = this._calculateScopeIndex(codeArray[index], token)
      if (scopeIndex !== -1) {
        return scopeIndex
      }
      return null
    } else {
      return null
    }
  }

  /**
   * Calculate the scope index and make sure it is valid (look at _checkScopeIndex for more info)
   *
   * @param string The string that maybe contains the scope enter / exit token.
   * @param token The scope enter / exit token to look for in the string.
   * @returns {*} The position in the line where the scope starts or ends or
   *              -1 if this line does neither.
   * @private
   */
  _calculateScopeIndex(string, token) {
    return this._checkScopeIndex(string, string.indexOf(token), token)
  }

  /**
   *  Calculate the scope index and make sure it is valid, that means:
   *    1. It is not inside quotation marks
   *
   *  If any of the above criteria is not met, the piece of code in question is cut off from the string,
   *  and we start over with the remaining string.
   *
   * @param string The string that maybe contains the scope enter / exit token.
   * @param scopeIndex The current "potential" scope index
   * @param token The scope enter / exit token to look for in the string.
   * @returns {*} The position in the line where the scope starts or ends or
   *              -1 if this line does neither.
   * @private
   */
  _checkScopeIndex(string, scopeIndex, token) {
    if (scopeIndex === -1) {
      return scopeIndex
    }

    // Check if scope identifier is inside quotation marks
    let quotationRegex = /(["'])(?:(?=(\\?))\2.)*?\1/
    let match = quotationRegex.exec(string)

    if (match === null || match[0].indexOf(token) === -1) {
      return scopeIndex
    }

    let matchEnd = match.index + match[0].length
    return this._calculateScopeIndex(string.substr(matchEnd), token)
  }

  /**
   * Checks if a line contains a special statement.
   *
   * A special statement is defined in Java by:
   * - An empty line (e.g. '')
   * - A line that starts with an annotation (e.g. '@')
   * - A line that starts with a comment (e.g. '//')
   *
   * @param line The line to check for a special statement.
   * @returns {Boolean} True if the line contains a special statement, else false.
   * @private
   */
  _checkForSpecialStatement(line) {
    return line.startsWith(ANNOTATION_TOKEN)
      || line === ''
      || COMMENT_TOKENS.reduce((result, token) => result || line.startsWith(token), false)
  }

  /**
   * Checks if a line is a method signature.
   *
   * @param line The line to check for.
   * @returns {Boolean} True if the line is a method signature, else false.
   * @private
   */
  _checkForFunction(line) {
    if (PROTECTED_NON_METHOD_TOKENS.reduce((result, token) => result || line.trim()
        .startsWith(token), false)) {
      return false
    }

    return this.methodSigRegex.test(line)
  }
}

import JavaFormatter from "./formatters/javaFormatter"

/**
 * The entry point of the module. Here formatters for various languages can be created.
 * As of right now, only Java is available.
 *
 * The <code>formatUnit</code> is always the token to be used for line indentations (ex. 2 spaces and 4 spaces).
 *
 * Available functions:
 * - <pre><code>JavaFormatter createJavaFormatter(formatUnit:String)</code></pre>
 *
 */
const Formatter = {
  /**
   * Create an auto-formatter for java code.
   *
   * @param formatUnit The token to be used for line indentations. (ex. "  " or "    ")
   * @returns {JavaFormatter} Auto-formatter for java code.
   */
  createJavaFormatter(formatUnit) {
    return new JavaFormatter(formatUnit)
  }
}

export default Formatter
